#include <stdio.h>

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    int a[N];
    for (int i = 0; i < N; i++) {
        scanf("%d", &a[i]);
    }

    int sum = 0;
    for (int i = 0; i < N; i++) {
        sum += a[i];
    }

    if (sum > K) {
        printf("Second\n");
    } else if (sum == K) {
        printf("First\n");
    } else {
        int turn = 0;
        int stones = K;
        while (1) {
            if (turn % 2 == 0) {
                if (stones >= a[0]) {
                    stones -= a[0];
                } else {
                    printf("First\n");
                    return 0;
                }
            } else {
                int i;
                for (i = 0; i < N; i++) {
                    if (stones >= a[i]) {
                        stones -= a[i];
                        break;
                    }
                }
                if (i == N) {
                    printf("Second\n");
                    return 0;
                }
            }
            turn++;
        }
    }

    return 0;
}
