#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int N, K;
    cin >> N >> K;

    vector<int> A(N);
    for (int i = 0; i < N; i++) {
        cin >> A[i];
    }

    int turn = 0;
    int stones = K;
    while (true) {
        int minStone = stones;
        for (int i = 0; i < N; i++) {
            if (A[i] <= minStone) {
                minStone = A[i];
            }
        }

        if (minStone > stones) {
            cout << "Second" << endl;
            return 0;
        }

        if (turn % 2 == 0) {
            stones -= minStone;
            if (minStone == 1) {
                cout << "First" << endl;
                return 0;
            }
        } else {
            stones += minStone;
        }

        turn++;
    }

    return 0;
}
