#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    int a[n];
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }
    int sum = 0;
    for (int i = 0; i < n; i++) {
        int max = a[i];
        int max_index = i;
        for (int j = i + 1; j < n; j++) {
            if (a[j] > max) {
                max = a[j];
                max_index = j;
            }
        }
        int temp = a[i];
        a[i] = a[max_index];
        a[max_index] = temp;
        int max2 = a[i];
        int max2_index = i;
        for (int j = i + 1; j < n; j++) {
            if (a[j] > max2) {
                max2 = a[j];
                max2_index = j;
            }
        }
        int temp2 = a[i];
        a[i] = a[max2_index];
        a[max2_index] = temp2;
        sum += a[i];
    }
    printf("%d\n", sum);
    return 0;
}
