#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

long long comb(int n, int k) {
    long long res = 1;
    for (int i = 1; i <= k; i++) {
        res = (res * (n - i + 1)) % MOD;
        res = (res * pow(i, MOD - 2)) % MOD;
    }
    return res;
}

long long pow(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = (res * a) % MOD;
        a = (a * a) % MOD;
        b >>= 1;
    }
    return res;
}

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    int a[n + 1];
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }
    long long ans = 1;
    for (int i = 1; i <= n; i++) {
        if (a[i] > k) {
            printf("0\n");
            return 0;
        }
        ans = (ans * comb(k - a[i], a[i])) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}
