#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const long long mod = 1e9 + 7;

long long nCr(long long n, long long r) {
    if (r > n) return 0;
    vector<long long> fact(n + 1);
    fact[0] = 1;
    for (long long i = 1; i <= n; i++) {
        fact[i] = (fact[i - 1] * i) % mod;
    }
    return (fact[n] * pow(fact[r], mod - 2) % mod * pow(fact[n - r], mod - 2) % mod) % mod;
}

long long solve(vector<long long>& a, long long k) {
    long long n = a.size();
    long long sum = accumulate(a.begin(), a.end(), 0LL);
    if (sum > k) return 0;
    long long ans = 1;
    for (long long i = 0; i < n; i++) {
        long long diff = k - a[i];
        long long ways = nCr(diff + a[i], a[i]);
        ans = (ans * ways) % mod;
    }
    return ans;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    long long n, k;
    cin >> n >> k;
    vector<long long> a(n);
    for (long long& x : a) cin >> x;
    cout << solve(a, k) << '\n';

    return 0;
}
