#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

int main() {
    int n;
    scanf("%d", &n);

    int a[n][n];
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            scanf("%d", &a[i][j]);
        }
    }

    int dp[1 << n];
    for (int i = 0; i < (1 << n); i++) {
        dp[i] = 0;
    }

    dp[0] = 1;

    for (int i = 0; i < (1 << n); i++) {
        for (int j = 0; j < n; j++) {
            if ((i & (1 << j)) == 0 && a[i][j] == 1) {
                int next = i | (1 << j);
                dp[next] = (dp[next] + dp[i]) % MOD;
            }
        }
    }

    int ans = 0;
    for (int i = 0; i < (1 << n); i++) {
        if ((i & (i - 1)) == 0) {
            ans = (ans + dp[i]) % MOD;
        }
    }

    printf("%d\n", ans);

    return 0;
}
