#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MOD = 1e9 + 7;

int main() {
    int n;
    cin >> n;

    vector<vector<int>> a(n, vector<int>(n));
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            cin >> a[i][j];
        }
    }

    vector<vector<int>> dp(n, vector<int>(1 << n));
    for (int i = 0; i < n; i++) {
        dp[i][1 << i] = 1;
    }

    for (int mask = 0; mask < (1 << n); mask++) {
        for (int i = 0; i < n; i++) {
            if (dp[i][mask] == 0) {
                continue;
            }
            for (int j = 0; j < n; j++) {
                if (a[i][j] == 0) {
                    continue;
                }
                int newMask = mask | (1 << j);
                if (newMask == ((1 << n) - 1)) {
                    cout << (dp[i][mask] + dp[i][mask]) % MOD << endl;
                    return 0;
                }
                dp[j][newMask] = (dp[j][newMask] + dp[i][mask]) % MOD;
            }
        }
    }

    return 0;
}
