import sys
input = sys.stdin.readline

mod = 10**9 + 7

def pow_mod(a, b):
    res = 1
    while b:
        if b & 1:
            res = res * a % mod
        a = a * a % mod
        b >>= 1
    return res

def solve():
    n = int(input())
    a = [list(map(int, input().split())) for _ in range(n)]
    dp = [[0] * (1 << n) for _ in range(n)]
    dp[0][1 << (a[0][0] - 1)] = 1
    for i in range(1, n):
        for mask in range(1 << n):
            if dp[i - 1][mask]:
                for j in range(n):
                    if a[i][j] and not (mask & (1 << j)):
                        dp[i][mask | (1 << j)] = (dp[i][mask | (1 << j)] + dp[i - 1][mask]) % mod
    ans = 0
    for mask in range(1 << n):
        if (mask & ((1 << n) - 1)) == ((1 << n) - 1):
            ans = (ans + dp[n - 1][mask]) % mod
    print(ans)

t = 1
for _ in range(t):
    solve()
