#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

typedef struct {
    int num;
    int* adj;
} Graph;

void dfs(Graph* g, int u, int p, int* dp) {
    for (int v : g->adj[u]) {
        if (v != p) {
            dfs(g, v, u, dp);
            dp[u] = (1LL * (dp[u] + dp[v] + 1) % MOD) * 2 % MOD;
        }
    }
}

int main() {
    int N;
    scanf("%d", &N);

    Graph* g = (Graph*)malloc(sizeof(Graph));
    g->num = N;
    g->adj = (int*)malloc(N * sizeof(int));

    for (int i = 0; i < N - 1; i++) {
        int u, v;
        scanf("%d %d", &u, &v);
        u--; v--;
        g->adj[u] = (int)realloc(g->adj[u], sizeof(int) * (g->adj[u] ? g->adj[u][0] + 1 : 1));
        g->adj[u][g->adj[u][0]] = v;
        g->adj[v] = (int)realloc(g->adj[v], sizeof(int) * (g->adj[v][0] + 1));
        g->adj[v][g->adj[v][0]] = u;
    }

    int* dp = (int*)malloc(N * sizeof(int));
    for (int i = 0; i < N; i++) {
        dp[i] = 2;
    }

    dfs(g, 0, -1, dp);

    printf("%d\n", dp[0]);

    free(g->adj);
    free(g);
    free(dp);

    return 0;
}
