#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

struct Edge {
    int to;
    int id;
};

int dfs(int u, int p, vector<vector<Edge>>& graph, vector<int>& dp) {
    int res = 1;
    for (auto& e : graph[u]) {
        if (e.to == p) continue;
        res = (res * (2 * dfs(e.to, u, graph, dp) % MOD)) % MOD;
    }
    dp[u] = (res + 1) % MOD;
    return dp[u];
}

int main() {
    int n;
    cin >> n;
    vector<vector<Edge>> graph(n + 1);
    for (int i = 0; i < n - 1; i++) {
        int u, v;
        cin >> u >> v;
        graph[u].push_back({v, i});
        graph[v].push_back({u, i});
    }
    vector<int> dp(n + 1);
    dfs(1, -1, graph, dp);
    long long res = 0;
    for (int i = 0; i < n - 1; i++) {
        int u, v;
        cin >> u >> v;
        if (u == v) continue;
        int x = dp[u];
        int y = dp[v];
        res = (res + (x * y) % MOD) % MOD;
    }
    cout << res << endl;
    return 0;
}
