#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int height;
    int beauty;
} Flower;

int compare(const void *a, const void *b) {
    Flower *f1 = (Flower *)a;
    Flower *f2 = (Flower *)b;
    return f1->height - f2->height;
}

int main() {
    int n;
    scanf("%d", &n);

    Flower *flowers = (Flower *)malloc(n * sizeof(Flower));
    for (int i = 0; i < n; i++) {
        scanf("%d", &flowers[i].height);
    }
    for (int i = 0; i < n; i++) {
        scanf("%d", &flowers[i].beauty);
    }

    qsort(flowers, n, sizeof(Flower), compare);

    long long sum = 0;
    int max_height = 0;
    for (int i = 0; i < n; i++) {
        if (flowers[i].height > max_height) {
            sum += flowers[i].beauty;
            max_height = flowers[i].height;
        }
    }

    printf("%lld\n", sum);

    free(flowers);
    return 0;
}
