#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<int> h(n), a(n);
    for (int i = 0; i < n; i++) {
        cin >> h[i];
    }
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    vector<pair<int, int>> ha(n);
    for (int i = 0; i < n; i++) {
        ha[i] = {h[i], a[i]};
    }

    sort(ha.begin(), ha.end());

    vector<int> dp(n + 1, 0);
    for (int i = 0; i < n; i++) {
        int j = lower_bound(ha.begin(), ha.end(), make_pair(ha[i].first, 0)) - ha.begin();
        dp[i + 1] = max(dp[i + 1], dp[i] + ha[i].second);
        if (j > 0) {
            dp[i + 1] = max(dp[i + 1], dp[j - 1] + ha[i].second);
        }
    }

    cout << dp[n] << endl;

    return 0;
}
