import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] h = new int[n];
        int[] a = new int[n];

        for (int i = 0; i < n; i++) {
            h[i] = scanner.nextInt();
        }

        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextInt();
        }

        int[] hIndex = new int[n];
        for (int i = 0; i < n; i++) {
            hIndex[h[i] - 1] = i;
        }

        long[] dp = new long[n];
        Arrays.fill(dp, Long.MIN_VALUE);

        for (int i = 0; i < n; i++) {
            if (i == 0) {
                dp[i] = a[i];
            } else {
                dp[i] = Math.max(dp[i - 1], a[i] + (i > 0 ? dp[i - 1] : 0));
            }
        }

        long maxSum = 0;
        int maxIndex = -1;
        for (int i = 0; i < n; i++) {
            if (dp[i] > maxSum) {
                maxSum = dp[i];
                maxIndex = i;
            }
        }

        int[] result = new int[n];
        int j = n - 1;
        for (int i = maxIndex; i >= 0; i--) {
            if (hIndex[i] < j) {
                result[hIndex[i]] = 1;
                j = hIndex[i];
            }
        }

        long sum = 0;
        for (int i = 0; i < n; i++) {
            if (result[i] == 1) {
                sum += a[i];
            }
        }

        System.out.println(sum);
    }
}
