#include <iostream>
#include <vector>
#include <cstring>

using namespace std;

const int N = 55;
const int M = 1e9 + 7;

int n, k;
int a[N][N];
int f[N][N];
int g[N][N];

int main() {
    cin >> n >> k;
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            cin >> a[i][j];
        }
    }

    memset(f, 0, sizeof(f));
    memset(g, 0, sizeof(g));

    for (int i = 1; i <= n; i++) {
        f[i][i] = 1;
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= n; j++) {
            for (int t = 1; t <= n; t++) {
                if (a[j][t]) {
                    g[i][t] = (g[i][t] + f[i][j]) % M;
                }
            }
        }
        memcpy(f, g, sizeof(g));
        memset(g, 0, sizeof(g));
    }

    int ans = 0;
    for (int i = 1; i <= n; i++) {
        ans = (ans + f[i][i]) % M;
    }

    if (k > 1) {
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                if (i != j) {
                    ans = (ans + f[i][j] * f[j][i] % M * (pow(2, k - 2) - 1 + M) % M) % M;
                }
            }
        }
    }

    cout << ans << endl;

    return 0;
}
