import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 55;
    static int[][] a = new int[MAXN][MAXN];
    static int[][] dp = new int[MAXN][MAXN];
    static int[][] f = new int[MAXN][MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());
        int n = Integer.parseInt(st.nextToken());
        long k = Long.parseLong(st.nextToken());
        for (int i = 1; i <= n; i++) {
            st = new StringTokenizer(br.readLine());
            for (int j = 1; j <= n; j++) {
                a[i][j] = Integer.parseInt(st.nextToken());
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                f[i][j] = a[i][j];
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                for (int k1 = 1; k1 <= n; k1++) {
                    f[j][k1] = (f[j][k1] + f[i][k1] * a[i][j]) % MOD;
                }
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                dp[i][j] = 1;
            }
        }
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                for (int k1 = 1; k1 <= n; k1++) {
                    dp[j][k1] = (dp[j][k1] + dp[i][k1] * a[i][j]) % MOD;
                }
            }
        }
        long ans = 0;
        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                ans = (ans + dp[i][j] * f[j][i] % MOD * pow(k - 2, i, MOD)) % MOD;
            }
        }
        pw.println(ans);
        pw.close();
    }

    static long pow(long a, long b, int mod) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = res * a % mod;
            }
            a = a * a % mod;
            b >>= 1;
        }
        return res;
    }
}
