import sys
input = sys.stdin.readline

mod = 10**9 + 7

def pow_mod(a, b):
    res = 1
    while b:
        if b & 1:
            res = res * a % mod
        a = a * a % mod
        b >>= 1
    return res

def main():
    n, k = map(int, input().split())
    a = [list(map(int, input().split())) for _ in range(n)]

    dp = [[0] * n for _ in range(n)]
    for i in range(n):
        dp[i][i] = 1

    for _ in range(k - 1):
        ndp = [[0] * n for _ in range(n)]
        for i in range(n):
            for j in range(n):
                for k in range(n):
                    if a[i][k] == 1:
                        ndp[i][j] = (ndp[i][j] + dp[k][j]) % mod
        dp = ndp

    ans = 0
    for i in range(n):
        for j in range(n):
            ans = (ans + dp[i][j]) % mod

    print(ans)

if __name__ == '__main__':
    main()
