#include <stdio.h>
#include <string.h>
#include <stdlib.h>

#define MOD 1000000007
#define N 100000

int dp[N + 1][101];
int sum[N + 1];

int main() {
    int k, d;
    scanf("%d %d", &k, &d);

    for (int i = 1; i <= k; i++) {
        int len = 0;
        int num = i;
        while (num) {
            sum[++len] = num % 10;
            num /= 10;
        }

        for (int j = 0; j <= d; j++) {
            dp[i][j] = 0;
        }

        for (int j = 1; j <= len; j++) {
            for (int l = 0; l <= d; l++) {
                if (l % d == 0 && (sum[j] + l / d) % d == 0) {
                    dp[i][l] = (dp[i][l] + dp[i - 1][l - (sum[j] + l / d)]) % MOD;
                }
            }
        }

        for (int j = 0; j <= d; j++) {
            dp[i][j] = (dp[i][j] + dp[i - 1][j]) % MOD;
        }
    }

    printf("%d\n", dp[k][0]);

    return 0;
}
