#include <stdio.h>
#include <stdlib.h>
#include <math.h>

typedef struct {
    int x;
    int v;
} sushi;

int cmp(const void *a, const void *b) {
    return ((sushi *)a)->x - ((sushi *)b)->x;
}

int main() {
    int N;
    long long C;
    scanf("%d%lld", &N, &C);

    sushi *s = (sushi *)malloc(N * sizeof(sushi));
    for (int i = 0; i < N; i++) {
        scanf("%d%d", &s[i].x, &s[i].v);
    }

    qsort(s, N, sizeof(sushi), cmp);

    long long max = 0;
    long long sum = 0;
    long long dist = 0;
    for (int i = 0; i < N; i++) {
        dist = (s[i].x - s[0].x + C) % C;
        sum = s[i].v - dist;
        if (sum > max) {
            max = sum;
        }
    }

    printf("%lld\n", max);

    free(s);
    return 0;
}
