#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n, c;
    cin >> n >> c;

    vector<pair<int, int>> sushi(n);
    for (int i = 0; i < n; i++) {
        cin >> sushi[i].first >> sushi[i].second;
    }

    sort(sushi.begin(), sushi.end());

    long long maxNutrition = 0;
    for (int i = 0; i < n; i++) {
        long long nutrition = 0;
        long long distance = 0;
        for (int j = i; j < n; j++) {
            nutrition += sushi[j].second;
            distance += sushi[j].first - sushi[i].first;
            if (distance > c) {
                break;
            }
            if (distance + sushi[j].first > c) {
                nutrition -= (distance + sushi[j].first - c) * (sushi[j].first - sushi[i].first);
                break;
            }
        }
        maxNutrition = max(maxNutrition, nutrition - distance);
    }

    cout << maxNutrition << endl;

    return 0;
}
