import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        long c = scanner.nextLong();
        int[] x = new int[n];
        int[] v = new int[n];
        for (int i = 0; i < n; i++) {
            x[i] = scanner.nextInt();
            v[i] = scanner.nextInt();
        }
        long[] pre = new long[n + 1];
        for (int i = 1; i <= n; i++) {
            pre[i] = pre[i - 1] + x[i - 1];
        }
        long ans = 0;
        for (int i = 0; i < n; i++) {
            long max = c - pre[i] + pre[n] - pre[i + 1];
            long min = pre[i + 1] - pre[i];
            long res = Math.max(0, v[i] - (max - min));
            ans = Math.max(ans, res);
        }
        System.out.println(ans);
    }
}
