#include <stdio.h>
#include <stdlib.h>

#define MAX_N 100005
#define MAX_M 100005

int n, m;
int s[MAX_M], t[MAX_M];
int in[MAX_N], out[MAX_N];
int vis[MAX_N];
int cnt;

int dfs(int u) {
    vis[u] = 1;
    for (int i = 0; i < cnt; i++) {
        if (in[u] & (1 << i)) {
            if (!vis[t[i]]) {
                if (dfs(t[i])) return 1;
            }
        }
    }
    return 0;
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 0; i < m; i++) {
        scanf("%d %d", &s[i], &t[i]);
        in[s[i]] |= (1 << i);
        out[t[i]] |= (1 << i);
    }
    for (int i = 0; i < n; i++) {
        if (!vis[i]) {
            cnt = 0;
            for (int j = 0; j < m; j++) {
                if (in[i] & (1 << j)) {
                    cnt++;
                }
            }
            if (dfs(i)) {
                printf("%d\n", cnt - 1);
                return 0;
            }
        }
    }
    printf("0\n");
    return 0;
}
