#include <stdio.h>
#include <stdlib.h>
#include <math.h>

typedef struct {
    int x;
    int v;
} sushi;

int cmp(const void *a, const void *b) {
    return ((sushi *)a)->x - ((sushi *)b)->x;
}

int main() {
    int N;
    long long C;
    scanf("%d%lld", &N, &C);

    sushi *s = (sushi *)malloc(N * sizeof(sushi));
    for (int i = 0; i < N; i++) {
        scanf("%d%d", &s[i].x, &s[i].v);
    }

    qsort(s, N, sizeof(sushi), cmp);

    long long max = 0;
    for (int i = 0; i < N; i++) {
        long long dist = s[i].x;
        long long energy = dist;
        long long nutrition = 0;
        for (int j = i + 1; j < N; j++) {
            dist += C;
            if (dist > C) dist -= C;
            energy += dist;
            nutrition += s[j].v;
            if (energy > nutrition) break;
            max = max > nutrition - energy ? max : nutrition - energy;
        }
    }

    printf("%lld\n", max);
    free(s);
    return 0;
}
