import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        long c = scanner.nextLong();
        long[] x = new long[n];
        long[] v = new long[n];
        for (int i = 0; i < n; i++) {
            x[i] = scanner.nextLong();
            v[i] = scanner.nextLong();
        }
        long[] x2 = new long[n];
        for (int i = 0; i < n; i++) {
            x2[i] = (x[i] + c) % c;
        }
        long[] v2 = new long[n];
        for (int i = 0; i < n; i++) {
            v2[i] = v[i];
        }
        long[] sum = new long[n + 1];
        for (int i = 0; i < n; i++) {
            sum[i + 1] = sum[i] + v2[i];
        }
        long[] pre = new long[n + 1];
        for (int i = 0; i < n; i++) {
            pre[i + 1] = pre[i] + x2[i];
        }
        long ans = 0;
        for (int i = 0; i < n; i++) {
            long l = 0, r = n;
            while (l < r) {
                long mid = (l + r) / 2;
                if (pre[mid] - pre[i] + (c - pre[mid] + pre[i]) % c <= x2[i]) {
                    r = mid;
                } else {
                    l = mid + 1;
                }
            }
            ans = Math.max(ans, sum[r] - sum[i] - (pre[r] - pre[i] + (c - pre[r] + pre[i]) % c));
        }
        System.out.println(ans);
    }
}
