#include <iostream>
#include <vector>
#include <queue>
#include <unordered_map>

using namespace std;

const int MAX_N = 1e5 + 5;

int n, m;
vector<int> g[MAX_N];
int in[MAX_N];
bool vis[MAX_N];
int dist[MAX_N];

void bfs(int s) {
    queue<int> q;
    q.push(s);
    vis[s] = true;
    dist[s] = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : g[u]) {
            if (!vis[v]) {
                vis[v] = true;
                dist[v] = dist[u] + 1;
                q.push(v);
            }
        }
    }
}

int main() {
    cin >> n >> m;
    for (int i = 0; i < m; i++) {
        int s, t;
        cin >> s >> t;
        g[s].push_back(t);
        in[t]++;
    }

    int ans = 0;
    for (int i = 0; i < n; i++) {
        if (in[i] == 0) {
            bfs(i);
            for (int j = 0; j < n; j++) {
                if (dist[j] % 2 == 1) {
                    ans++;
                }
            }
        }
    }

    cout << ans << endl;

    return 0;
}
