#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

const int MAX_N = 3000;

long long modInv(long long a, long long m) {
    long long m0 = m, y = 0, x = 1;
    if (m == 1) return 0;
    while (a > 1) {
        long long q = a / m;
        long long t = m;
        m = a % m, a = t;
        t = y;
        y = x - q * y;
        x = t;
    }
    if (x < 0) x += m0;
    return x;
}

long long binomialCoeff(long long n, long long k, long long mod) {
    vector<long long> fact(n + 1);
    fact[0] = 1;
    for (int i = 1; i <= n; i++) {
        fact[i] = (fact[i - 1] * i) % mod;
    }
    return (fact[n] * modInv(fact[k], mod) % mod * modInv(fact[n - k], mod) % mod) % mod;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int N;
    long long M;
    cin >> N >> M;

    long long ans = 0;
    for (int i = 0; i <= N; i++) {
        long long ways = binomialCoeff(N, i);
        if (i % 2 == 1) {
            ways = (M - ways) % M;
        }
        ways = (ways * ways) % M;
        ans = (ans + ways) % M;
    }

    cout << (ans + M - 1) % M << endl;

    return 0;
}
