import java.io.*;
import java.util.*;

public class Main {
    static final int N = 3005;
    static long[] fac = new long[N];
    static long[] inv = new long[N];
    static long mod;

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        PrintWriter pw = new PrintWriter(System.out);
        int n = sc.nextInt();
        mod = sc.nextLong();
        fac[0] = 1;
        for (int i = 1; i <= n; i++) {
            fac[i] = fac[i - 1] * i % mod;
        }
        inv[n] = pow(fac[n], mod - 2);
        for (int i = n - 1; i >= 0; i--) {
            inv[i] = inv[i + 1] * (i + 1) % mod;
        }
        long ans = 0;
        for (int i = 1; i < (1 << n); i++) {
            int cnt = 0;
            for (int j = 0; j < n; j++) {
                if ((i & (1 << j)) != 0) {
                    cnt++;
                }
            }
            if (cnt % 2 == 0) {
                continue;
            }
            long tmp = 1;
            for (int j = 0; j < n; j++) {
                if ((i & (1 << j)) != 0) {
                    tmp = tmp * inv[cnt - 1] % mod * fac[cnt - 2] % mod;
                }
            }
            ans = (ans + tmp) % mod;
        }
        pw.println(ans);
        pw.close();
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) != 0) {
                res = res * a % mod;
            }
            a = a * a % mod;
            b >>= 1;
        }
        return res;
    }
}

