#include <stdio.h>
#include <stdlib.h>
#include <limits.h>

#define N 50
#define INF INT_MAX

int main() {
    int n, x, d;
    int m[N + 1];
    int p[N + 1];
    int dp[N + 1][N + 1];
    int minp[N + 1];
    int maxp[N + 1];
    int i, j, k;

    scanf("%d %d %d", &n, &x, &d);
    for (i = 1; i <= n; i++) {
        scanf("%d", &m[i]);
    }
    for (i = 2; i <= n; i++) {
        scanf("%d", &p[i]);
    }

    for (i = 1; i <= n; i++) {
        minp[i] = INF;
        maxp[i] = -INF;
    }
    for (i = 2; i <= n; i++) {
        minp[p[i]] = min(minp[p[i]], i - 1);
        maxp[p[i]] = max(maxp[p[i]], i - 1);
    }

    for (i = 1; i <= n; i++) {
        for (j = 0; j <= x; j++) {
            dp[i][j] = 0;
        }
    }
    for (i = 1; i <= n; i++) {
        for (j = 0; j <= x; j++) {
            if (j >= m[i]) {
                dp[i][j] = max(dp[i][j], dp[i - 1][j - m[i]] + 1);
            }
            if (i > 1 && j >= m[i] && minp[i] <= i - 1 && dp[i - 1][j - m[i]] + 1 <= maxp[i]) {
                dp[i][j] = max(dp[i][j], dp[i - 1][j - m[i]] + 1 + d);
            }
        }
    }

    int ans = 0;
    for (i = 0; i <= x; i++) {
        ans = max(ans, dp[n][i]);
    }
    printf("%d\n", ans);

    return 0;
}
