#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAXN = 55;

int N, X, D;
int m[MAXN];
int p[MAXN];
int dp[MAXN][MAXN];

int main() {
    cin >> N >> X >> D;
    for (int i = 1; i <= N; i++) {
        cin >> m[i];
    }
    for (int i = 2; i <= N; i++) {
        cin >> p[i];
    }

    for (int i = 1; i <= N; i++) {
        for (int j = 1; j <= N; j++) {
            dp[i][j] = -1;
        }
    }

    dp[1][1] = X / m[1];

    for (int i = 2; i <= N; i++) {
        for (int j = 1; j <= N; j++) {
            if (dp[i - 1][j] != -1) {
                int l = max(j, dp[i - 1][j]);
                int r = min(dp[i - 1][j] + D, dp[i - 1][j] + (X / m[i]) - 1);
                for (int k = l; k <= r; k++) {
                    if (dp[i][k] == -1 || dp[i][k] < dp[i - 1][j] + k - j) {
                        dp[i][k] = dp[i - 1][j] + k - j;
                    }
                }
            }
        }
    }

    int ans = 0;
    for (int i = 1; i <= N; i++) {
        ans = max(ans, dp[N][i]);
    }

    cout << ans << endl;

    return 0;
}
