#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 200005

int cmp(const void *a, const void *b) {
    return (*(int *)a - *(int *)b);
}

int main() {
    int n;
    scanf("%d", &n);
    int x[MAXN];
    for (int i = 0; i < n; i++) {
        scanf("%d", &x[i]);
    }
    qsort(x, n, sizeof(int), cmp);
    for (int i = 0; i < n; i++) {
        int l = 0, r = n - 1;
        int j = i;
        while (l <= r) {
            int mid = (l + r) / 2;
            if (mid < j) {
                l = mid + 1;
            } else if (mid > j) {
                r = mid - 1;
            } else {
                break;
            }
        }
        int k = (n + 1) / 2;
        if (j < k) {
            printf("%d\n", x[j]);
        } else {
            printf("%d\n", x[k - 1]);
        }
    }
    return 0;
}
