#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int value;
    int index;
} Pair;

int comparePairs(const void *a, const void *b) {
    Pair *pairA = (Pair *)a;
    Pair *pairB = (Pair *)b;
    return pairB->value - pairA->value;
}

int main() {
    int n;
    scanf("%d", &n);
    Pair *pairs = (Pair *)malloc(n * sizeof(Pair));
    for (int i = 0; i < n; i++) {
        scanf("%d", &pairs[i].value);
        pairs[i].index = i;
    }
    qsort(pairs, n, sizeof(Pair), comparePairs);
    int maxComb = 0;
    int maxIndex = -1;
    int maxIndex2 = -1;
    for (int i = 0; i < n - 1; i++) {
        int comb = pairs[i].value;
        for (int j = i + 1; j < n; j++) {
            comb = (comb * (pairs[j].value - 1)) / (pairs[i].value);
            if (comb > maxComb) {
                maxComb = comb;
                maxIndex = pairs[i].index;
                maxIndex2 = pairs[j].index;
            }
        }
    }
    printf("%d %d\n", pairs[maxIndex].value, pairs[maxIndex2].value);
    free(pairs);
    return 0;
}
