import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = scanner.nextInt();
        }
        Arrays.sort(a);
        int max = 0;
        int i = n - 1;
        int j = 0;
        while (i > j) {
            int comb = (int) Math.comb(a[i], a[j]);
            if (comb > max) {
                max = comb;
                int temp = a[i];
                a[i] = a[j];
                a[j] = temp;
                i--;
                j++;
            } else if (comb == max) {
                i--;
                j++;
            } else {
                j++;
            }
        }
        System.out.println(a[i] + " " + a[j]);
    }

    public static long MathComb(int n, int r) {
        if (r > n - r) {
            r = n - r;
        }
        long result = 1;
        for (int i = 0; i < r; i++) {
            result = result * (n - i) / (i + 1);
        }
        return result;
    }
}
