#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAXN 200005

int cmp(const void *a, const void *b) {
    return (*(int *)a - *(int *)b);
}

int main() {
    int n;
    scanf("%d", &n);
    int x[n + 1];
    for (int i = 1; i <= n; i++) {
        scanf("%d", &x[i]);
    }
    qsort(x + 1, n, sizeof(int), cmp);
    for (int i = 1; i <= n; i++) {
        int l = (i + n) / 2;
        int r = (i + n) / 2 + 1;
        if (i == 1) {
            printf("%d\n", x[l]);
        } else if (i == n) {
            printf("%d\n", x[l - 1]);
        } else {
            if (x[l] == x[r]) {
                printf("%d\n", x[l]);
            } else if (x[l] > x[r]) {
                printf("%d\n", x[r]);
            } else {
                printf("%d\n", x[l]);
            }
        }
    }
    return 0;
}
