import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] x = new int[n];
        for (int i = 0; i < n; i++) {
            x[i] = scanner.nextInt();
        }
        Arrays.sort(x);
        for (int i = 0; i < n; i++) {
            int[] y = new int[n - 1];
            System.arraycopy(x, 0, y, 0, i);
            System.arraycopy(x, i + 1, y, i, n - i - 1);
            int median = getMedian(y);
            System.out.println(median);
        }
    }

    public static int getMedian(int[] arr) {
        int n = arr.length;
        if (n % 2 == 0) {
            int mid1 = arr[n / 2 - 1];
            int mid2 = arr[n / 2];
            return (mid1 + mid2) / 2;
        } else {
            return arr[n / 2];
        }
    }
}
