#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int value;
    int index;
} Pair;

int compare(const void *a, const void *b) {
    Pair *pairA = (Pair *)a;
    Pair *pairB = (Pair *)b;
    return pairB->value - pairA->value;
}

int main() {
    int n;
    scanf("%d", &n);
    Pair *pairs = (Pair *)malloc(n * sizeof(Pair));
    for (int i = 0; i < n; i++) {
        scanf("%d", &pairs[i].value);
        pairs[i].index = i;
    }
    qsort(pairs, n, sizeof(Pair), compare);
    int maxComb = 0;
    int maxIndex1 = -1;
    int maxIndex2 = -1;
    for (int i = 0; i < n; i++) {
        for (int j = i + 1; j < n; j++) {
            int comb = 1;
            int min = pairs[i].value;
            int max = pairs[j].value;
            for (int k = 2; k <= min; k++) {
                if (min % k == 0) {
                    comb *= k;
                }
            }
            for (int k = 2; k <= max; k++) {
                if (max % k == 0) {
                    comb *= k;
                }
            }
            if (comb > maxComb) {
                maxComb = comb;
                maxIndex1 = pairs[i].index;
                maxIndex2 = pairs[j].index;
            }
        }
    }
    printf("%d %d\n", pairs[maxIndex1].value, pairs[maxIndex2].value);
    free(pairs);
    return 0;
}
