#include <stdio.h>
#include <stdlib.h>

#define N 100005
#define K 100005

int n, k;
int head[N], to[K * 2], nxt[K * 2], val[K * 2], tot;
int size[N], edge[N][N];

void add(int x, int y, int z) {
    to[++tot] = y;
    nxt[tot] = head[x];
    head[x] = tot;
    val[tot] = z;
    to[++tot] = x;
    nxt[tot] = head[y];
    head[y] = tot;
    val[tot] = z;
}

void dfs(int x, int fa) {
    for (int i = head[x]; i; i = nxt[i]) {
        int y = to[i];
        if (y == fa) continue;
        size[y] = size[x] + val[i];
        dfs(y, x);
    }
}

int query(int x, int y) {
    int res = 0;
    while (x != y) {
        if (size[x] > size[y]) {
            for (int i = head[x]; i; i = nxt[i]) {
                int y = to[i];
                if (size[y] > size[x]) continue;
                if (val[i] % k == 0) continue;
                res += val[i];
            }
            x = edge[x][y];
        } else {
            for (int i = head[y]; i; i = nxt[i]) {
                int x = to[i];
                if (size[x] > size[y]) continue;
                if (val[i] % k == 0) continue;
                res += val[i];
            }
            y = edge[y][x];
        }
    }
    return res;
}

int main() {
    scanf("%d %d", &n, &k);
    for (int i = 1; i < n; i++) {
        int x, y, z;
        scanf("%d %d %d", &x, &y, &z);
        add(x, y, z);
    }
    for (int i = 1; i < n; i++) {
        for (int j = head[i]; j; j = nxt[j]) {
            int x = to[j];
            if (x > i) edge[i][x] = edge[x][i] = j;
        }
    }
    dfs(1, 0);
    int q;
    scanf("%d", &q);
    while (q--) {
        char op[10];
        int x, y;
        scanf("%s %d %d", op, &x, &y);
        if (op[0] == 'a') {
            for (int i = head[x]; i; i = nxt[i]) {
                int y = to[i];
                if (size[y] > size[x]) continue;
                val[i] += y;
            }
            dfs(x, 0);
        } else {
            printf("%d\n", query(x, y));
        }
    }
    return 0;
}
