#include <iostream>
#include <vector>
#include <queue>
#include <algorithm>

using namespace std;

const int MAX_N = 1e5 + 5;

int n, k;
vector<int> adj[MAX_N];
int size[MAX_N];
int charge[MAX_N];

void add(int x, int d) {
    for (int y : adj[x]) {
        size[y] += d;
        charge[y] += d;
    }
}

int query(int x, int y) {
    int res = 0;
    for (int z : adj[x]) {
        if (adj[y].end() == find(adj[y].begin(), adj[y].end(), z)) {
            res += min(size[z], charge[z]);
        }
    }
    for (int z : adj[y]) {
        if (adj[x].end() == find(adj[x].begin(), adj[x].end(), z)) {
            res += min(size[z], charge[z]);
        }
    }
    return res;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> k;
    for (int i = 1; i < n; i++) {
        int x, y, c;
        cin >> x >> y >> c;
        adj[x].push_back(y);
        adj[y].push_back(x);
        size[y] = size[x] = c;
        charge[y] = charge[x] = c;
    }

    int q;
    cin >> q;
    while (q--) {
        char op;
        cin >> op;
        if (op == 'a') {
            int x, d;
            cin >> x >> d;
            add(x, d);
        } else {
            int x, y;
            cin >> x >> y;
            cout << query(x, y) << '\n';
        }
    }

    return 0;
}
