#include <iostream>
#include <set>
#include <algorithm>

int main() {
    int A, B, K;
    std::cin >> A >> B >> K;

    std::set<int> S;
    for (int i = A; i <= B; ++i) {
        S.insert(i);
    }

    std::set<int> smallest(K);
    std::set<int> largest(K);

    for (auto it = S.begin(); it != S.end() && smallest.size() < K; ++it) {
        smallest.insert(*it);
    }

    for (auto it = S.rbegin(); it != S.rend() && largest.size() < K; ++it) {
        largest.insert(*it);
    }

    for (auto it = S.begin(); it != S.end(); ++it) {
        if (std::find(smallest.begin(), smallest.end(), *it) != smallest.end() ||
            std::find(largest.begin(), largest.end(), *it) != largest.end()) {
            std::cout << *it << " ";
        }
    }

    return 0;
}
