import java.io.*;
import java.util.*;

public class Main {
    static int N, K;
    static int[][] graph;
    static int[] size;
    static int[] sizeChange;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        StringTokenizer st = new StringTokenizer(br.readLine());
        N = Integer.parseInt(st.nextToken());
        K = Integer.parseInt(st.nextToken());

        graph = new int[N][];
        size = new int[N];
        sizeChange = new int[N];

        for (int i = 0; i < N - 1; i++) {
            st = new StringTokenizer(br.readLine());
            int a = Integer.parseInt(st.nextToken());
            int b = Integer.parseInt(st.nextToken());
            int c = Integer.parseInt(st.nextToken());
            graph[a] = add(graph[a], b, c);
            graph[b] = add(graph[b], a, c);
        }

        int Q = Integer.parseInt(br.readLine());
        while (Q-- > 0) {
            st = new StringTokenizer(br.readLine());
            String type = st.nextToken();
            if (type.equals("add")) {
                int x = Integer.parseInt(st.nextToken());
                int d = Integer.parseInt(st.nextToken());
                addSize(x, d);
            } else {
                int s = Integer.parseInt(st.nextToken());
                int t = Integer.parseInt(st.nextToken());
                System.out.println(query(s, t));
            }
        }
    }

    static int[] add(int[] arr, int val, int size) {
        if (arr == null) {
            arr = new int[1];
            arr[0] = val;
            return arr;
        }
        int[] newArr = new int[arr.length + 1];
        System.arraycopy(arr, 0, newArr, 0, arr.length);
        newArr[arr.length] = val;
        return newArr;
    }

    static void addSize(int x, int d) {
        if (size[x] == 0) return;
        for (int y : graph[x]) {
            if (size[y] == 0) continue;
            int i = 0;
            while (i < graph[y].length && graph[y][i] != x) i++;
            if (i < graph[y].length) {
                graph[y][i] += d;
                if (graph[y][i] % K == 0) graph[y][i] = 0;
            }
        }
        size[x] += d;
        if (size[x] % K == 0) size[x] = 0;
    }

    static int query(int s, int t) {
        int res = 0;
        for (int y : graph[s]) {
            if (y == t) continue;
            if (size[y] == 0) continue;
            int i = 0;
            while (i < graph[y].length && graph[y][i] != s) i++;
            if (i < graph[y].length) {
                res += graph[y][i];
                if (graph[y][i] % K == 0) res -= graph[y][i];
            }
        }
        for (int y : graph[t]) {
            if (y == s) continue;
            if (size[y] == 0) continue;
            int i = 0;
            while (i < graph[y].length && graph[y][i] != t) i++;
            if (i < graph[y].length) {
                res += graph[y][i];
                if (graph[y][i] % K == 0) res -= graph[y][i];
            }
        }
        return res;
    }
}
