#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int q;
    cin >> q;
    vector<long long> a(q), b(q);
    for (int i = 0; i < q; i++) {
        cin >> a[i] >> b[i];
    }
    for (int i = 0; i < q; i++) {
        long long x = a[i] - 1, y = b[i] - 1;
        long long ans = (x + 1) * (y + 1) - 1;
        cout << ans << endl;
    }
    return 0;
}
