#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int q;
    std::cin >> q;

    std::vector<long long> a(q), b(q);
    for (int i = 0; i < q; ++i) {
        std::cin >> a[i] >> b[i];
    }

    for (int i = 0; i < q; ++i) {
        long long x = a[i] - 1, y = b[i] - 1;
        long long ans = (x + y) * (x + y + 1) / 2 - x - y;
        std::cout << ans << std::endl;
    }

    return 0;
}
