#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int a, b;
} Pair;

int comparePairs(const void *a, const void *b) {
    Pair *p1 = (Pair *)a;
    Pair *p2 = (Pair *)b;
    if (p1->a == p2->a) {
        return p1->b - p2->b;
    }
    return p1->a - p2->a;
}

int main() {
    int n;
    scanf("%d", &n);
    Pair *pairs = (Pair *)malloc(n * sizeof(Pair));
    for (int i = 0; i < n; i++) {
        scanf("%d %d", &pairs[i].a, &pairs[i].b);
    }
    qsort(pairs, n, sizeof(Pair), comparePairs);
    long long sum = 0;
    int i = 0;
    while (i < n) {
        if (pairs[i].a == pairs[i].b) {
            sum += pairs[i].a;
            i++;
            continue;
        }
        int j = i;
        while (j < n && pairs[j].a == pairs[i].a) {
            if (pairs[j].b < pairs[i].b) {
                sum += pairs[j].b - pairs[i].b;
                pairs[j].b = pairs[i].b;
            }
            j++;
        }
        i = j;
    }
    printf("%lld\n", sum);
    free(pairs);
    return 0;
}
