#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n;
    cin >> n;

    vector<int> a(n), b(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i] >> b[i];
    }

    long long sum = 0;
    for (int i = 0; i < n; i++) {
        sum += a[i] + b[i];
    }

    long long ans = 0;
    int i = 0;
    while (i < n) {
        if (a[i] == b[i]) {
            i++;
            continue;
        }

        if (a[i] > b[i]) {
            int j = i;
            while (j < n && a[j] > b[j]) {
                a[j]--;
                b[j]--;
                ans++;
                j++;
            }
            i = j;
        } else {
            int j = i;
            while (j < n && a[j] < b[j]) {
                a[j]++;
                b[j]++;
                j++;
            }
            i = j;
        }
    }

    for (int i = 0; i < n; i++) {
        ans += min(a[i], b[i]);
    }

    cout << ans << endl;

    return 0;
}
