import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;
    static final int MAXN = (int) 2e5 + 5;

    static int n;
    static int[] f = new int[MAXN];
    static int[] g = new int[MAXN];
    static int[] h = new int[MAXN];
    static int[] a = new int[MAXN];
    static int[] b = new int[MAXN];
    static int[] c = new int[MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        String s = br.readLine();
        n = s.length();
        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == 'a') a[i] = 1;
            if (s.charAt(i) == 'b') b[i] = 1;
            if (s.charAt(i) == 'c') c[i] = 1;
        }
        f[0] = 1;
        g[0] = 1;
        h[0] = 1;
        for (int i = 1; i < n; i++) {
            if (a[i] == 1) {
                f[i] = (f[i - 1] + g[i - 1] + h[i - 1]) % MOD;
                g[i] = (g[i - 1] + h[i - 1]) % MOD;
                h[i] = h[i - 1];
            } else if (b[i] == 1) {
                f[i] = (f[i - 1] + g[i - 1] + h[i - 1]) % MOD;
                g[i] = (f[i - 1] + h[i - 1]) % MOD;
                h[i] = (g[i - 1] + h[i - 1]) % MOD;
            } else {
                f[i] = (f[i - 1] + g[i - 1] + h[i - 1]) % MOD;
                g[i] = f[i - 1] % MOD;
                h[i] = (g[i - 1] + h[i - 1]) % MOD;
            }
        }
        pw.println(f[n - 1]);
        pw.close();
    }
}
