#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

std::string nextDiverseWord(const std::string& word) {
    int n = word.size();
    for (int i = 0; i < n; ++i) {
        for (char c = word[i] + 1; c <= 'z'; ++c) {
            std::string newWord = word;
            newWord[i] = c;
            bool isDiverse = true;
            for (int j = 0; j < i; ++j) {
                if (newWord[j] == newWord[i]) {
                    isDiverse = false;
                    break;
                }
            }
            if (isDiverse) {
                return newWord;
            }
        }
    }
    return "-1";
}

int main() {
    std::string word;
    std::cin >> word;
    std::cout << nextDiverseWord(word) << std::endl;
    return 0;
}
