import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        String s = scanner.next();
        int n = s.length();
        int[] cnt = new int[26];
        for (int i = 0; i < n; i++) {
            cnt[s.charAt(i) - 'a']++;
        }
        int idx = 0;
        while (idx < n) {
            if (cnt[s.charAt(idx) - 'a'] == 1) {
                break;
            }
            cnt[s.charAt(idx) - 'a']--;
            idx++;
        }
        if (idx == n) {
            System.out.println(-1);
            return;
        }
        char c = s.charAt(idx);
        for (int i = c + 1; i <= 'z'; i++) {
            String t = s.substring(0, idx) + (char) i;
            boolean flag = true;
            for (int j = 0; j < 26; j++) {
                if (cnt[j] > 0) {
                    if (t.indexOf((char) (j + 'a')) != -1) {
                        flag = false;
                        break;
                    }
                }
            }
            if (flag) {
                System.out.println(t);
                return;
            }
        }
        System.out.println(-1);
    }
}
