import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int[] result = new int[n];
        result[0] = 2;
        result[1] = 5;
        result[2] = 63;
        for (int i = 3; i < n; i++) {
            result[i] = (result[i - 1] * 2) + 1;
        }
        for (int i = 0; i < n; i++) {
            System.out.print(result[i] + " ");
        }
    }
}
