#include <stdio.h>
#include <limits.h>

#define N 55

int main() {
    int n, a[N], b[N], dp[N][N][N];
    long long ans = 0;

    scanf("%d", &n);
    for (int i = 0; i < n; i++) scanf("%d", &a[i]);
    for (int i = 0; i < n; i++) scanf("%d", &b[i]);

    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            for (int k = 0; k < n; k++) {
                dp[i][j][k] = INT_MAX;
            }
        }
    }

    dp[0][0][0] = 0;

    for (int i = 1; i < n; i++) {
        for (int j = 0; j < n; j++) {
            for (int k = 0; k < n; k++) {
                if (dp[i - 1][j][k] != INT_MAX) {
                    for (int l = 1; l <= 60; l++) {
                        if (dp[i - 1][j][k] + (1LL << l) < dp[i][j][k]) {
                            dp[i][j][k] = dp[i - 1][j][k] + (1LL << l);
                            if (a[i] % l == b[i] % l) {
                                if (j > 0) {
                                    dp[i][j][k] = min(dp[i][j][k], dp[i - 1][j - 1][k]);
                                }
                                if (k > 0) {
                                    dp[i][j][k] = min(dp[i][j][k], dp[i - 1][j][k - 1]);
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    if (dp[n - 1][n - 1][n - 1] == INT_MAX) {
        printf("-1\n");
    } else {
        printf("%lld\n", dp[n - 1][n - 1][n - 1]);
    }

    return 0;
}
