#include <iostream>
#include <vector>
#include <algorithm>
#include <climits>

using namespace std;

typedef long long ll;

const int MAXN = 55;

int n;
int a[MAXN], b[MAXN];
ll dp[MAXN][1 << 15];

int main() {
    cin >> n;
    for (int i = 1; i <= n; i++) cin >> a[i];
    for (int i = 1; i <= n; i++) cin >> b[i];

    for (int i = 1; i <= n; i++) {
        for (int mask = 0; mask < (1 << 15); mask++) {
            dp[i][mask] = LLONG_MAX;
        }
    }

    dp[0][0] = 0;

    for (int i = 1; i <= n; i++) {
        for (int mask = 0; mask < (1 << 15); mask++) {
            if (dp[i - 1][mask] != LLONG_MAX) {
                for (int k = 1; k <= 15; k++) {
                    int newMask = mask;
                    if ((b[i] - a[i]) % k == 0) {
                        newMask |= (1 << (k - 1));
                    }
                    dp[i][newMask] = min(dp[i][newMask], dp[i - 1][mask] + (1LL << k));
                }
            }
        }
    }

    ll ans = LLONG_MAX;
    for (int mask = 0; mask < (1 << 15); mask++) {
        if (dp[n][mask] != LLONG_MAX) {
            ans = min(ans, dp[n][mask]);
        }
    }

    if (ans == LLONG_MAX) {
        cout << -1 << endl;
    } else {
        cout << ans << endl;
    }

    return 0;
}
