import java.io.*;
import java.util.*;

public class Main {
    static final int N = 55;
    static long[][] dp = new long[2][N];
    static int n;
    static int[] a = new int[N];
    static int[] b = new int[N];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st;

        st = new StringTokenizer(br.readLine());
        n = Integer.parseInt(st.nextToken());
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= n; i++) {
            a[i] = Integer.parseInt(st.nextToken());
        }
        st = new StringTokenizer(br.readLine());
        for (int i = 1; i <= n; i++) {
            b[i] = Integer.parseInt(st.nextToken());
        }

        dp[0][0] = 0;
        for (int i = 1; i <= n; i++) {
            for (int j = 0; j < N; j++) {
                dp[i & 1][j] = (long) 1e18;
            }
            for (int j = 0; j < N; j++) {
                if (dp[(i - 1) & 1][j] != (long) 1e18) {
                    for (int k = 1; k <= 30; k++) {
                        int x = (j + k) % N;
                        if (x == b[i]) {
                            dp[i & 1][x] = Math.min(dp[i & 1][x], dp[(i - 1) & 1][j] + (long) 1 << k);
                        }
                    }
                }
            }
        }

        long ans = (long) 1e18;
        for (int i = 0; i < N; i++) {
            ans = Math.min(ans, dp[n & 1][i]);
        }
        if (ans == (long) 1e18) {
            pw.println(-1);
        } else {
            pw.println(ans);
        }
        pw.close();
    }
}
