import sys
from collections import deque

def solve():
    n = int(input())
    a = list(map(int, input().split()))
    b = list(map(int, input().split()))

    if a != b:
        q = deque()
        q.append((0, 0))
        visited = set()
        visited.add((0, 0))

        while q:
            k, mask = q.popleft()
            if k > 50:
                continue
            new_mask = mask
            for i in range(n):
                if (a[i] ^ b[i]) & mask:
                    new_mask |= 1 << (a[i] % k)
            if new_mask == mask:
                continue
            if new_mask not in visited:
                visited.add(new_mask)
                q.append((k + 1, new_mask))
        if (0, 0) not in visited:
            print(-1)
            return
        ans = 0
        for k in range(1, 60):
            if (0, 0) in visited:
                break
            for mask in visited:
                if (mask >> k) & 1:
                    visited.remove(mask)
                    visited.add(mask ^ (1 << k))
                    ans += 1 << k
        print(ans)
    else:
        print(0)

t = 1
for _ in range(t):
    solve()
